CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `GestionarAsignaciones` (IN `p_accion` VARCHAR(50), IN `p_parametro` VARCHAR(255), IN `p_id_gestor` INT)   BEGIN
    IF p_accion = 'obtener' THEN
        -- Resumen de aldeas
        SELECT 
            Village AS aldea,
            COUNT(*) AS total_ninos,
            SUM(CASE WHEN Id_Gestor IS NOT NULL THEN 1 ELSE 0 END) AS ninos_asignados
        FROM nino
        GROUP BY Village;

    ELSEIF p_accion = 'obtener_ninos_aldea' THEN
        -- Niños por aldea
        SELECT 
            Child_Number,
            Full_Name,
            Id_Gestor IS NOT NULL AS esta_asignado
        FROM nino
        WHERE Village COLLATE utf8mb4_general_ci = p_parametro COLLATE utf8mb4_general_ci;

    ELSEIF p_accion = 'asignar' THEN
        -- Asignar niño a gestor
        UPDATE nino
        SET Id_Gestor = p_id_gestor
        WHERE Child_Number = CAST(p_parametro AS SIGNED)
          AND Id_Gestor IS NULL;

    ELSEIF p_accion = 'obtener_ninos_asignados' THEN
        -- Total de niños por gestor
        SELECT 
            COUNT(*) AS total_ninos
        FROM nino
        WHERE Id_Gestor = p_id_gestor;
    END IF;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `GestionarNiños` (IN `accion` VARCHAR(30), IN `p_child_number` INT, IN `p_full_name` VARCHAR(255), IN `p_village_id` INT, IN `p_birthdate` DATE, IN `p_community_number` VARCHAR(255), IN `p_gender` VARCHAR(25), IN `p_sponsorship_status` VARCHAR(100), IN `p_enrolled_on_date` DATE, IN `p_local_partner` VARCHAR(255), IN `p_alliance_name` VARCHAR(255), IN `p_primary_contact_full_name` VARCHAR(255), IN `p_mes_seleccionado` DATE)   BEGIN
    IF accion = 'insertar' THEN
            child_number, full_name, village_id, birthdate, community_number, gender,
            sponsorship_status, enrolled_on_date, local_partner, alliance_name,
            primary_contact_full_name, mes_seleccionado
        ) VALUES (
            p_child_number, p_full_name, p_village_id, p_birthdate, p_community_number, p_gender,
            p_sponsorship_status, p_enrolled_on_date, p_local_partner, p_alliance_name,
            p_primary_contact_full_name, p_mes_seleccionado
        )
        ON DUPLICATE KEY UPDATE
            full_name = VALUES(full_name),
            village_id = VALUES(village_id),
            birthdate = VALUES(birthdate),
            community_number = VALUES(community_number),
            gender = VALUES(gender),
            sponsorship_status = VALUES(sponsorship_status),
            enrolled_on_date = VALUES(enrolled_on_date),
            local_partner = VALUES(local_partner),
            alliance_name = VALUES(alliance_name),
            primary_contact_full_name = VALUES(primary_contact_full_name),
            mes_seleccionado = VALUES(mes_seleccionado);

    ELSEIF accion = 'actualizar' THEN
        UPDATE nino
        SET 
            full_name = p_full_name,
            village_id = p_village_id,
            birthdate = p_birthdate,
            community_number = p_community_number,
            gender = p_gender,
            sponsorship_status = p_sponsorship_status,
            enrolled_on_date = p_enrolled_on_date
        WHERE child_number = p_child_number;

    ELSEIF accion = 'obtener' THEN
        SELECT 
            n.community_number,
            v.nombre AS village,
            n.child_number AS Child_Number,
            n.full_name,
            n.gender,
            n.birthdate,
            n.sponsorship_status,
            n.enrolled_on_date,
            n.local_partner,
            n.alliance_name,
            n.primary_contact_full_name
        FROM nino n
        LEFT JOIN village v ON v.id = n.village_id;

    ELSEIF accion = 'obtenerPorMes' THEN
        SELECT 
            n.community_number,
            v.nombre AS village,
            n.child_number AS Child_Number,
            n.full_name,
            n.gender,
            n.birthdate,
            n.sponsorship_status,
            n.enrolled_on_date,
            n.local_partner,
            n.alliance_name,
            n.primary_contact_full_name
        FROM nino n
        LEFT JOIN village v ON v.id = n.village_id
        WHERE n.mes_seleccionado = p_mes_seleccionado
        ORDER BY n.enrolled_on_date DESC;

    ELSEIF accion = 'obtenerMesesDisponibles' THEN
        SELECT 
            mes_seleccionado,
            COUNT(*) AS cantidad
        FROM nino
        GROUP BY mes_seleccionado
        ORDER BY mes_seleccionado DESC;
    END IF;
