CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `ObtenerAdministradores` (IN `p_roles` VARCHAR(255))   BEGIN
   -- Por el momento ya no se esta utilizando
    DECLARE done INT DEFAULT 0;
    DECLARE rol_actual VARCHAR(100);

    -- Cursor para separar los roles
    DECLARE cur_roles CURSOR FOR 
        SELECT TRIM(SUBSTRING_INDEX(SUBSTRING_INDEX(p_roles, ',', numbers.n), ',', -1)) AS role
        FROM (
            SELECT 1 n UNION SELECT 2 UNION SELECT 3 UNION SELECT 4 UNION SELECT 5
            UNION SELECT 6 UNION SELECT 7 UNION SELECT 8 UNION SELECT 9 UNION SELECT 10
        ) numbers
        WHERE n <= (LENGTH(p_roles) - LENGTH(REPLACE(p_roles, ',', '')) + 1);

    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;

    -- Crear tabla temporal con COLLATE adecuado
    CREATE TEMPORARY TABLE tmp_roles (
        rol VARCHAR(100) COLLATE utf8mb4_unicode_ci
    );

    OPEN cur_roles;

    leer_roles: LOOP
        FETCH cur_roles INTO rol_actual;
        IF done THEN
            LEAVE leer_roles;
        END IF;
    END LOOP;

    CLOSE cur_roles;

    -- Consulta final corrigiendo el COLLATE
    SELECT g.Cedula, g.nombre_completo
    FROM gestor g
    INNER JOIN gestor_rol gr ON g.Cedula = gr.gestor_id
    INNER JOIN rol r ON gr.rol_id = r.id
    WHERE r.tipo_rol COLLATE utf8mb4_unicode_ci IN (SELECT rol FROM tmp_roles)
    GROUP BY g.Cedula;

    DROP TEMPORARY TABLE IF EXISTS tmp_roles;
END$$

