CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `ObtenerInformeAnualT` (IN `anioSeleccionado` YEAR)   BEGIN
    SELECT 
        COUNT(*) AS total_cartas_por_enviar,
        SUM(CASE WHEN YEAR(fecha_recepcion) = anioSeleccionado THEN 1 ELSE 0 END) AS cartas_recibidas_anio,
        SUM(CASE WHEN YEAR(fecha_envio) = anioSeleccionado THEN 1 ELSE 0 END) AS enviadas_anio_actual,
        SUM(CASE WHEN fecha_envio > fecha_vencimiento THEN 1 ELSE 0 END) AS enviadas_con_retraso,
        ROUND((SUM(CASE WHEN fecha_envio > fecha_vencimiento THEN 1 ELSE 0 END) * 100) / COUNT(*), 2) AS porcentaje_retraso,
        ROUND((SUM(CASE WHEN fecha_envio IS NOT NULL THEN ABS(DATEDIFF(fecha_envio, fecha_vencimiento)) ELSE 0 END) / 
               COUNT(CASE WHEN fecha_envio IS NOT NULL THEN 1 ELSE NULL END)), 2) AS promedio_cumplimiento_dias_envio,
        SUM(CASE WHEN fecha_envio IS NULL AND fecha_vencimiento >= CURDATE() THEN 1 ELSE 0 END) AS por_enviar_a_fecha,
        SUM(CASE WHEN fecha_envio IS NULL AND fecha_vencimiento < CURDATE() THEN 1 ELSE 0 END) AS por_enviar_fuera_tiempo,
        COUNT(DISTINCT id_gestor) AS total_promotores_gestores,
        tc.tipocarta AS tipo_carta_nombre
    FROM carta c
    LEFT JOIN tipocarta tc ON c.tipoCarta = tc.codigo
    WHERE YEAR(fecha_mcs) = anioSeleccionado
    GROUP BY tc.tipocarta;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `ObtenerInformeMensual` (IN `mes` VARCHAR(7))   BEGIN
    IF mes NOT REGEXP '^[0-9]{4}-(0[1-9]|1[0-2])$' THEN
        SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Formato de mes inválido';
    END IF;
    SELECT 
        COUNT(*) AS total_cartas_por_enviar,
        SUM(CASE WHEN DATE_FORMAT(fecha_recepcion, '%Y-%m') = mes THEN 1 ELSE 0 END) AS cartas_recibidas_mes,
        SUM(CASE WHEN DATE_FORMAT(fecha_envio, '%Y-%m') = mes THEN 1 ELSE 0 END) AS enviadas_mes_actual,
        SUM(CASE WHEN DATE_FORMAT(fecha_envio, '%Y-%m') = DATE_FORMAT(DATE_SUB(STR_TO_DATE(CONCAT(mes, '-01'), '%Y-%m-%d'), INTERVAL 1 MONTH), '%Y-%m') THEN 1 ELSE 0 END) AS enviadas_mes_anterior,
        SUM(CASE WHEN fecha_envio > fecha_vencimiento THEN 1 ELSE 0 END) AS enviadas_con_retraso,
        ROUND((SUM(CASE WHEN fecha_envio > fecha_vencimiento THEN 1 ELSE 0 END) * 100) / COUNT(*), 2) AS porcentaje_retraso,
        ROUND((SUM(CASE WHEN fecha_envio IS NOT NULL THEN ABS(DATEDIFF(fecha_envio, fecha_vencimiento)) ELSE 0 END) / 
               COUNT(CASE WHEN fecha_envio IS NOT NULL THEN 1 ELSE NULL END)), 2) AS promedio_cumplimiento_dias_envio,
        SUM(CASE WHEN fecha_envio IS NULL AND fecha_vencimiento >= CURDATE() THEN 1 ELSE 0 END) AS por_enviar_a_fecha,
        SUM(CASE WHEN fecha_envio IS NULL AND fecha_vencimiento < CURDATE() THEN 1 ELSE 0 END) AS por_enviar_fuera_tiempo,
        COUNT(DISTINCT id_gestor) AS total_promotores_gestores,
        tc.tipocarta AS tipo_carta_nombre
    FROM carta c
    LEFT JOIN tipocarta tc ON c.tipoCarta = tc.codigo
    WHERE DATE_FORMAT(fecha_mcs, '%Y-%m') = mes
    GROUP BY tc.tipocarta;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `ObtenerInformeSemestralT` (IN `semestreSeleccionado` VARCHAR(7))   BEGIN
    DECLARE fecha_inicio DATE;
    DECLARE fecha_fin DATE;
    IF semestreSeleccionado NOT REGEXP '^[0-9]{4}-(1|2)$' THEN
        SIGNAL SQLSTATE '45000'
            SET MESSAGE_TEXT = 'Formato de semestre inválido';
    END IF;
    IF SUBSTRING_INDEX(semestreSeleccionado, '-', -1) = '1' THEN
        SET fecha_inicio = CONCAT(SUBSTRING(semestreSeleccionado, 1, 4), '-01-01');
        SET fecha_fin = CONCAT(SUBSTRING(semestreSeleccionado, 1, 4), '-06-30');
    ELSE
        SET fecha_inicio = CONCAT(SUBSTRING(semestreSeleccionado, 1, 4), '-07-01');
        SET fecha_fin = CONCAT(SUBSTRING(semestreSeleccionado, 1, 4), '-12-31');
    END IF;
    SELECT 
        COUNT(*) AS total_cartas_por_enviar,
        SUM(CASE WHEN YEAR(fecha_recepcion) = SUBSTRING(semestreSeleccionado, 1, 4) AND DATE(fecha_recepcion) BETWEEN fecha_inicio AND fecha_fin THEN 1 ELSE 0 END) AS cartas_recibidas_semestre,
        SUM(CASE WHEN YEAR(fecha_envio) = SUBSTRING(semestreSeleccionado, 1, 4) AND DATE(fecha_envio) BETWEEN fecha_inicio AND fecha_fin THEN 1 ELSE 0 END) AS enviadas_semestre_actual,
        SUM(CASE WHEN fecha_envio > fecha_vencimiento AND DATE(fecha_envio) BETWEEN fecha_inicio AND fecha_fin THEN 1 ELSE 0 END) AS enviadas_con_retraso,
        ROUND((SUM(CASE WHEN fecha_envio > fecha_vencimiento THEN 1 ELSE 0 END) * 100) / NULLIF(COUNT(*),0), 2) AS porcentaje_retraso,
        ROUND((SUM(CASE WHEN fecha_envio IS NOT NULL THEN ABS(DATEDIFF(fecha_envio, fecha_vencimiento)) ELSE 0 END) / NULLIF(COUNT(CASE WHEN fecha_envio IS NOT NULL THEN 1 ELSE NULL END),0)), 2) AS promedio_cumplimiento_dias_envio,
        SUM(CASE WHEN fecha_envio IS NULL AND fecha_vencimiento >= CURDATE() AND DATE(fecha_vencimiento) BETWEEN fecha_inicio AND fecha_fin THEN 1 ELSE 0 END) AS por_enviar_a_fecha,
        SUM(CASE WHEN fecha_envio IS NULL AND fecha_vencimiento < CURDATE() AND DATE(fecha_vencimiento) BETWEEN fecha_inicio AND fecha_fin THEN 1 ELSE 0 END) AS por_enviar_fuera_tiempo,
        COUNT(DISTINCT id_gestor) AS total_promotores_gestores,
        tc.tipocarta AS tipo_carta_nombre
    FROM carta c
    LEFT JOIN tipocarta tc ON c.tipoCarta = tc.codigo
    WHERE (DATE(fecha_recepcion) BETWEEN fecha_inicio AND fecha_fin
           OR DATE(fecha_envio) BETWEEN fecha_inicio AND fecha_fin
           OR DATE(fecha_vencimiento) BETWEEN fecha_inicio AND fecha_fin)
    GROUP BY tc.tipocarta;
END$$

