        SUM(CASE WHEN c.id_gestor IS NOT NULL THEN 1 ELSE 0 END) AS cartas_asignadas,
        (COUNT(c.codigo_mcs) - SUM(CASE WHEN c.id_gestor IS NOT NULL THEN 1 ELSE 0 END)) AS faltantes
    FROM carta c
    INNER JOIN tipocarta tc ON c.tipoCarta = tc.codigo
    INNER JOIN nino n ON c.Child_Number = n.Child_Number
    WHERE 
        (p_gestor_id = 0 OR n.village_id IN (
            SELECT village_id FROM gestor_village WHERE gestor_id = p_gestor_id
        ))
        AND c.estado_envio = 'pendiente'  -- ✅ Reemplazo de fecha_envio
    GROUP BY tc.tipocarta, tc.codigo
    ORDER BY tc.tipocarta;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `ObtenerResumenVillages` ()   BEGIN
    SELECT 
