CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `ObtenerSemestresDisponibles` ()   BEGIN
    SELECT DISTINCT 
        CONCAT(YEAR(fecha_recepcion), '-', 
            CASE 
                WHEN MONTH(fecha_recepcion) BETWEEN 1 AND 6 THEN '1'
                WHEN MONTH(fecha_recepcion) BETWEEN 7 AND 12 THEN '2'
            END) AS semestre_disponible
    FROM carta
    WHERE fecha_recepcion IS NOT NULL
    ORDER BY semestre_disponible DESC;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `ObtenerTodosLosVillages` ()   BEGIN
