CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_asignarCartasAHojaRuta` (IN `p_hoja_ruta_id` INT, IN `p_codigo_mcs` VARCHAR(50))   BEGIN
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        SELECT 0 AS filas_afectadas;
    END;

    IF EXISTS (SELECT 1 FROM hoja_ruta WHERE id = p_hoja_ruta_id) THEN
        UPDATE carta
        SET hojaRuta = p_hoja_ruta_id,
            estado_envio = 'enviada' -- ✅ Igual que el comportamiento original
        WHERE codigo_mcs = p_codigo_mcs;
    END IF;

    SELECT ROW_COUNT() AS filas_afectadas;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_cerrarHojaRuta` (IN `p_hoja_ruta_id` INT, IN `p_usuario_id` INT)   BEGIN
    DECLARE v_filas_hr INT DEFAULT 0;

    -- ✅ Cerrar la Hoja de Ruta
    UPDATE hoja_ruta
    SET 
        estado = 'cerrada',
        cerrado_por = p_usuario_id,
        fecha_cierre = NOW()
    WHERE id = p_hoja_ruta_id AND LOWER(estado) = 'abierta';

    -- ✅ Guardar filas afectadas antes de actualizar cartas
    SET v_filas_hr = ROW_COUNT();

    -- ✅ Actualizar las cartas asociadas solo si la HR se cerró correctamente
    IF v_filas_hr > 0 THEN
        UPDATE carta
        SET estado_envio = 'enviada'
        WHERE hojaRuta = p_hoja_ruta_id
          AND estado_envio = 'asignada';
    END IF;

    -- ✅ Devolver filas afectadas en el cierre de la HR
    SELECT v_filas_hr AS filas_afectadas;
END$$

