CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_crudMotivosDevolucion` (IN `p_accion` VARCHAR(50), IN `p_id` INT, IN `p_descripcion` VARCHAR(255), IN `p_estado` TINYINT)   BEGIN
