CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_crudObservaciones` (IN `accion` VARCHAR(50), IN `pIdObservacion` INT, IN `pDescripcionObservacion` VARCHAR(255), IN `pDetalleObservacion` TEXT)   BEGIN
    IF accion = 'insertarObservacion' THEN
        VALUES (pDescripcionObservacion, pDetalleObservacion);
    ELSEIF accion = 'actualizarObservacion' THEN
        UPDATE observaciones
        SET descripcion = pDescripcionObservacion,
            detalle = pDetalleObservacion
        WHERE idObservaciones = pIdObservacion;
    ELSEIF accion = 'eliminarObservacion' THEN
        DELETE FROM observaciones WHERE idObservaciones = pIdObservacion;
    ELSEIF accion = 'mostrarObservaciones' THEN
        SELECT idObservaciones, descripcion, detalle
        FROM observaciones;
    ELSEIF accion = 'obtener_por_id' THEN
        SELECT idObservaciones, descripcion, detalle
        FROM observaciones
        WHERE idObservaciones = pIdObservacion
        LIMIT 1;
    END IF;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_crudPlanesFuturos` (IN `accion` VARCHAR(50), IN `pIdPlanFuturo` VARCHAR(10), IN `pDescripcionPlanFuturo` VARCHAR(255), IN `pDetallePlanFuturo` TEXT)   BEGIN
