CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_crudUsuariosELIMINAR` (IN `accion` VARCHAR(50), IN `pCedula` INT, IN `pNombreCompleto` VARCHAR(255), IN `pCorreoElectronico` VARCHAR(255), IN `pIdRol` INT, IN `pContrasena` VARCHAR(255))   BEGIN
    IF accion = 'insertar' THEN
        VALUES (pCedula, pNombreCompleto, pCorreoElectronico, pIdRol, pContrasena);
    ELSEIF accion = 'actualizar' THEN
        UPDATE gestor
        SET nombre_completo = pNombreCompleto,
            correo_electronico = pCorreoElectronico,
            id_rol = pIdRol,
            contrasena = pContrasena
        WHERE Cedula = pCedula;
    ELSEIF accion = 'eliminar' THEN
        DELETE FROM gestor WHERE Cedula = pCedula;
    ELSEIF accion = 'mostrar' THEN
        SELECT g.Cedula, g.nombre_completo, g.correo_electronico, r.tipo_rol
        FROM gestor g
        JOIN rol r ON g.id_rol = r.id;
    ELSEIF accion = 'obtenerPorCedula' THEN
        SELECT Cedula, nombre_completo, correo_electronico, id_rol
        FROM gestor
        WHERE Cedula = pCedula
        LIMIT 1;
    END IF;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_getCartasPorTipo` (IN `p_tipoCarta` INT, IN `p_gestor_id` INT)   BEGIN
    SELECT 
        n.village_id AS village,                        -- ✅ VILLAGE
        c.Child_Number AS child_number,                 -- ✅ CHILD NUMBER
        n.full_name AS nombres,                         -- ✅ NOMBRES (usando el campo correcto)
        c.codigo_mcs AS mcs,                            -- ✅ MCS
        NULL AS dfc_amount,                             -- ✅ DFC Amount (puedes actualizarlo si tienes lógica)
        tc.tipocarta AS tipo_carta,                     -- ✅ TIPO DE CARTA
        c.fecha_recepcion,                              -- ✅ FECHA DE RECEPCIÓN
        DATEDIFF(CURDATE(), c.fecha_recepcion) AS dias_transcurridos, -- ✅ DÍAS TRANSCURRIDOS
        c.id_gestor,
        c.fecha_mcs,
        c.fecha_vencimiento,
        c.fecha_envio
    FROM carta c
    INNER JOIN tipocarta tc ON c.tipoCarta = tc.codigo
    INNER JOIN nino n ON c.Child_Number = n.Child_Number
    WHERE 
        c.tipoCarta = p_tipoCarta
        AND c.fecha_envio IS NULL                       -- ✅ Solo cartas sin enviar
        AND (p_gestor_id = 0 OR n.village_id IN (
            SELECT village_id 
            FROM gestor_village 
            WHERE gestor_id = p_gestor_id
        ))
    ORDER BY dias_transcurridos DESC;                   -- ✅ Ordenadas por más días transcurridos
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_getCartasPorTipo_Procesar` (IN `p_tipoCarta` INT, IN `p_gestor_id` INT)   BEGIN
    SELECT
        v.nombre AS village,
        n.Child_Number AS child_number,
        n.full_name AS nombres,
        c.codigo_mcs AS mcs,
        c.dfc_amount,
        tc.tipocarta AS tipo_carta,
        c.fecha_recepcion,
        DATEDIFF(CURDATE(), c.fecha_recepcion) AS dias_transcurridos,
        c.punto_focal -- ✅ NUEVO: Motivo de devolución del Punto Focal
    FROM carta c
    INNER JOIN nino n ON c.Child_Number = n.Child_Number
    INNER JOIN village v ON n.village_id = v.id
    INNER JOIN tipocarta tc ON c.tipoCarta = tc.codigo
    WHERE 
        c.tipoCarta = p_tipoCarta
        AND c.estado_envio = 'pendiente'  -- ✅ Solo cartas pendientes (devueltas o nuevas)
        AND (p_gestor_id = 0 OR n.village_id IN (
            SELECT village_id FROM gestor_village WHERE gestor_id = p_gestor_id
        ))
    ORDER BY dias_transcurridos DESC;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_listarCartasParaDevolucionExterna` ()   BEGIN
