CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_listarCartasParaHojaRuta` (IN `p_organizacion_id` INT)   BEGIN
    SELECT
        c.codigo_mcs,
        n.full_name AS nombres,
        v.nombre AS village,
        c.tipo_envio,
        CASE 
            WHEN c.punto_focal LIKE 'DEVUELTO%' THEN 'reasignada'
            ELSE 'nueva'
        END AS tipo_asignacion
    FROM carta c
    INNER JOIN nino n ON c.Child_Number = n.Child_Number
    INNER JOIN village v ON n.village_id = v.id
    WHERE 
        c.estado_envio = 'lista'
        AND (p_organizacion_id = 0 OR v.organizacion_id = p_organizacion_id)
    ORDER BY tipo_asignacion DESC, c.fecha_recepcion ASC;
