CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_listarHistorialDevolucionesExternas` (IN `p_fecha_inicio` DATE, IN `p_fecha_fin` DATE, IN `p_numero_hr` VARCHAR(20), IN `p_motivo_id` INT)   BEGIN
    SELECT 
        d.id,
        d.codigo_mcs,
        v.nombre AS village,
        n.full_name AS nombres,
        tc.tipocarta AS tipo_carta,
        hr.numero_hr,
        d.fecha_devolucion,
        o.descripcion AS motivo,
        d.detalle,
        g.nombre_completo AS devuelta_por,
        d.fecha_reenvio
    FROM devolucion_externa d
    INNER JOIN carta c ON d.codigo_mcs = c.codigo_mcs
    INNER JOIN nino n ON c.Child_Number = n.Child_Number
    INNER JOIN village v ON n.village_id = v.id
    INNER JOIN tipocarta tc ON c.tipoCarta = tc.codigo
    LEFT JOIN hoja_ruta hr ON d.hoja_ruta_id = hr.id
    LEFT JOIN observaciones o ON d.motivo_id = o.idObservaciones
    LEFT JOIN gestor g ON d.devuelta_por = g.Cedula
    WHERE (p_fecha_inicio IS NULL OR d.fecha_devolucion >= p_fecha_inicio)
      AND (p_fecha_fin IS NULL OR d.fecha_devolucion <= p_fecha_fin)
      AND (p_numero_hr IS NULL OR hr.numero_hr = p_numero_hr)
      AND (p_motivo_id IS NULL OR d.motivo_id = p_motivo_id)
    ORDER BY d.fecha_devolucion DESC;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_listarHojasDeRuta` (IN `p_organizacion_id` INT)   BEGIN
    SELECT 
