CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_planificacion` (IN `accion` VARCHAR(20), IN `p1` INT, IN `p2` INT, IN `p3` VARCHAR(255), IN `p4` TEXT, IN `p5` DATE, IN `p6` TIME, IN `p7` TIME, IN `p8` VARCHAR(100), IN `p9` VARCHAR(50), IN `p10` DATE, IN `p11` DATE)   BEGIN
  IF accion = 'listar_usuario' THEN
    SELECT * FROM planificacion WHERE id_gestor = p1;

  ELSEIF accion = 'listar_global' THEN
    SELECT p.*, g.Nombre_Completo
    FROM planificacion p
    INNER JOIN gestor g ON p.id_gestor = g.Cedula
    WHERE p.fecha BETWEEN p10 AND p11;

  ELSEIF accion = 'insertar' THEN
    IF p2 IS NOT NULL AND p3 IS NOT NULL AND p5 IS NOT NULL THEN
        id_gestor, titulo, descripcion, fecha,
        hora_inicio, hora_fin, tipo_actividad, estado
      ) VALUES (
        p2, p3, p4, p5,
        p6, p7, p8, p9
      );
    END IF;

  ELSEIF accion = 'editar' THEN
    IF p1 IS NOT NULL AND p3 IS NOT NULL AND p5 IS NOT NULL THEN
      UPDATE planificacion SET
        titulo = p3,
        descripcion = p4,
        fecha = p5,
        hora_inicio = p6,
        hora_fin = p7,
        tipo_actividad = p8,
        estado = p9
      WHERE id = p1;
    END IF;

  ELSEIF accion = 'eliminar' THEN
    IF p1 IS NOT NULL THEN
      DELETE FROM planificacion WHERE id = p1;
    END IF;
  END IF;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_reabrirHojaRuta` (IN `p_hoja_ruta_id` INT, IN `p_usuario_id` INT)   BEGIN
