CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_reporteEstadisticoHR` (IN `p_organizacion_id` INT, IN `p_fecha_inicio` DATE, IN `p_fecha_fin` DATE)   BEGIN
    SELECT 
        o.Alias AS organizacion,
        c.tipo_envio,
        COUNT(*) AS total_cartas
    FROM carta c
    LEFT JOIN hoja_ruta h ON c.hojaRuta = h.id
    LEFT JOIN organizacion o ON h.organizacion_id = o.id
    WHERE (p_organizacion_id = 0 OR h.organizacion_id = p_organizacion_id)
      AND h.fecha_envio BETWEEN p_fecha_inicio AND p_fecha_fin
    GROUP BY o.Alias, c.tipo_envio
    ORDER BY o.Alias, c.tipo_envio;
