CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `sp_tipocarta_operations` (IN `operacion` INT, IN `p_codigo` VARCHAR(10), IN `p_tipocarta` VARCHAR(50), IN `p_tiempo_estimado` INT)   BEGIN
    IF operacion = 1 THEN
        SELECT codigo, tipocarta, tiempo_estimado FROM tipocarta;
    ELSEIF operacion = 2 THEN
        UPDATE tipocarta
        SET tiempo_estimado = p_tiempo_estimado
        WHERE codigo = p_codigo;
    ELSEIF operacion = 3 THEN
        SELECT codigo, tipocarta FROM tipocarta;
    ELSEIF operacion = 4 THEN
        VALUES (p_tipocarta, p_tiempo_estimado);
    END IF;
END$$

CREATE DEFINER=`u424644346_mramos`@`127.0.0.1` PROCEDURE `verificar_roles` (IN `cedula_usuario` VARCHAR(20))   BEGIN
    SELECT r.tipo_rol
    FROM gestor_rol gr
    JOIN rol r ON gr.rol_id = r.id
    WHERE gr.gestor_id = cedula_usuario;
END$$

